import React, { useState } from "react";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";

const DropdownMenu = () => {
  const [eventsMenuAnchor, setEventsMenuAnchor] = useState(null);

  const handleEventsMenuOpen = (event) => {
    setEventsMenuAnchor(event.currentTarget);
  };

  const handleEventsMenuClose = () => {
    setEventsMenuAnchor(null);
  };

  return (
    <div className="dropdown-menu">
      <Button
        color="inherit"
        onMouseEnter={handleEventsMenuOpen}
        onMouseLeave={handleEventsMenuClose}
      >
        Events
      </Button>
      <Menu
        anchorEl={eventsMenuAnchor}
        open={Boolean(eventsMenuAnchor)}
        onClose={handleEventsMenuClose}
      >
        <MenuItem component={Link} to="/upcoming" onClick={handleEventsMenuClose}>
          Upcoming
        </MenuItem>
        <MenuItem component={Link} to="/past" onClick={handleEventsMenuClose}>
          Past
        </MenuItem>
      </Menu>
    </div>
  );
};

export default DropdownMenu;
