import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Layout = () => {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleMouseEnter = () => {
    setShowDropdown(true);
  };

  const handleMouseLeave = () => {
    setShowDropdown(false);
  };

  return (
    <>
      <nav
        style={{
          position: 'absolute',
          top: 0,
          right: 0,
          padding: '1rem',
          display: 'flex',
          justifyContent: 'flex-end',
          alignItems: 'center',
        }}
      >
        <ul style={{ listStyle: 'none', display: 'flex', alignItems: 'center' }}>
          <li>
            <Link to="/" style={{ color: '#fff', textDecoration: 'none', margin: '0 1rem' }}>
              Home
            </Link>
          </li>
          <li>
            <button
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              style={{
                color: '#fff',
                textDecoration: 'none',
                margin: '0 1rem',
                cursor: 'pointer',
                backgroundColor: 'transparent',
                border: 'none',
                padding: 0,
              }}
            >
              Events
              {showDropdown && (
                <div
                  onMouseEnter={handleMouseEnter}
                  onMouseLeave={handleMouseLeave}
                  style={{
                    backgroundColor: '#f0f0f0',
                    minWidth: '10rem',
                    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
                    position: 'absolute',
                    top: '100%',
                    right: 0,
                  }}
                >
                  <Link
                    to="/item1"
                    style={{ display: 'block', padding: '1rem', color: '#333', textDecoration: 'none' }}
                  >
                    Item 1
                  </Link>
                  <Link
                    to="/item2"
                    style={{ display: 'block', padding: '1rem', color: '#333', textDecoration: 'none' }}
                  >
                    Item 2
                  </Link>
                </div>
              )}
            </button>
          </li>
          <li>
            <Link to="/ourteam" style={{ color: '#fff', textDecoration: 'none', margin: '0 1rem' }}>
              Our Team
            </Link>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Layout;
