import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Layout = () => {
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <>
      <nav style={{ backgroundColor: '#333', padding: '1rem' }}>
        <ul style={{ listStyle: 'none', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <li>
            <Link to="/" style={{ color: '#fff', textDecoration: 'none', margin: '0 1rem' }}>
              Home
            </Link>
          </li>
          <li style={{ position: 'relative', display: 'inline-block' }}>
            <button onClick={toggleDropdown} style={{ color: '#fff', textDecoration: 'none', margin: '0 1rem', cursor: 'pointer' }}>
              Dropdown
            </button>
            {showDropdown && (
              <div style={{ backgroundColor: '#f0f0f0', minWidth: '10rem', boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)', position: 'absolute', top: '100%', left: '50%', transform: 'translateX(-50%)' }}>
                <Link to="/item1" style={{ display: 'block', padding: '1rem', color: '#333', textDecoration: 'none' }}>Item 1</Link>
                <Link to="/item2" style={{ display: 'block', padding: '1rem', color: '#333', textDecoration: 'none' }}>Item 2</Link>
                <Link to="/item3" style={{ display: 'block', padding: '1rem', color: '#333', textDecoration: 'none' }}>Item 3</Link>
              </div>
            )}
          </li>
          <li>
            <Link to="/ourteam" style={{ color: '#fff', textDecoration: 'none', margin: '0 1rem' }}>
              Our Team
            </Link>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Layout;
